namespace App\Http\Controllers;

use App\Models\Actor;
use App\Http\Requests\ActorRequest;
use Illuminate\Http\Request;
use Illuminate\Support\Facades\Http;
use App\Helpers\ResponseHelper;

class ActorController extends Controller
{
    public function index(Request $request)
    {
        $needle = $request->get('needle');

        try {
            $url = $needle ? "actors?needle=" . urlencode($needle) : "actors";

            $response = Http::api()->get($url);

            if ($response->failed()) {
                $message = $response->json('message') ?? 'Ismeretlen hiba történt.';
                return redirect()
                    ->route('actors.index')
                    ->with('error', "Hiba történt a lekérdezés során: $message");
            }

            $entities = ResponseHelper::getData($response,'actors');
            /*var_dump($entities);
            die;*/

            return view('actors.index', ['actors' => $entities, 'isAuthenticated' => $this->isAuthenticated()]);

        } catch (\Exception $e) {
            return redirect()
                ->route('actors.index')
                ->with('error', "Nem sikerült betölteni a megyéket: " . $e->getMessage());
        }

    }

    public function show($id)
    {
        try {
            $response = Http::api()->get("/actors/$id");

            if ($response->failed()) {
                $message = $response->json('message') ?? 'A film nem található vagy hiba történt.';
                return redirect()
                    ->route('actors.index')
                    ->with('error', "Hiba: $message");
            }

            $body = $response->json();
            $entity = $body['actor'] ?? null;

            if (!$entity) {
                return redirect()
                    ->route('actors.index')
                    ->with('error', "A megye adatai nem érhetők el.");
            }

            return view('actors.show', ['entity' => $entity]);

        } catch (\Exception $e) {
            return redirect()
                ->route('actors.index')
                ->with('error', "Nem sikerült betölteni a megye adatait: " . $e->getMessage());
        }
    }

    public function create()
    {
        return view('actors.create');
    }
	

    public function store(ActorRequest $request)
    {
        $name = $request->get('name');

        try {
            $response = Http::api()
                ->withToken($this->token)
                ->post('/actors', ['name' => $name]);

            if ($response->failed()) {
                // Ha az API válaszolt, de hibás státuszkóddal (pl. 422, 403, 500)
                $message = $response->json('message') ?? 'Nem sikerült létrehozni a megyét.';
                return redirect()
                    ->route('actors.index')
                    ->with('error', "Hiba: $message");
            }

            return redirect()
                ->route('actors.index')
                ->with('success', "$name megye sikeresen létrehozva!");

        } catch (\Exception $e) {
            // Hálózati vagy JSON dekódolási hiba
            return redirect()
                ->route('actors.index')
                ->with('error', "Nem sikerült kommunikálni az API-val: " . $e->getMessage());
        }

    }

	public function edit($id)
    {
        try {
            $response = Http::api()->get("/actors/$id");

            if ($response->failed()) {
                $message = $response->json('message') ?? 'A megye nem található vagy hiba történt.';
                return redirect()
                    ->route('actors.index')
                    ->with('error', "Hiba: $message");
            }

            $body = $response->json();
            $entity = $body['Actors'] ?? null;

            if (!$entity) {
                return redirect()
                    ->route('actors.index')
                    ->with('error', "A megye adatai nem érhetők el.");
            }

            return view('actors.edit', ['entity' => $entity]);

        } catch (\Exception $e) {
            return redirect()
                ->route('actors.index')
                ->with('error', "Nem sikerült betölteni a megye szerkesztő nézetét: " . $e->getMessage());
        }
    }


    public function update(ActorRequest $request, $id)
    {
        $name = $request->get('name');

        try {
            $response = Http::api()
                ->withToken($this->token)
                ->put("/actors/$id", ['name' => $name]);

            if ($response->successful()) {
                return redirect()
                    ->route('actors.index')
                    ->with('success', "$name megye sikeresen frissítve!");
            }

            // Ha nem sikeres, de nem dobott kivételt (pl. 422)
            $errorMessage = $response->json('message') ?? 'Ismeretlen hiba történt.';
            return redirect()
                ->route('actors.index')
                ->with('error', "Hiba történt: $errorMessage");

        } catch (\Exception $e) {
            // Hálózati vagy egyéb kivétel
            return redirect()
                ->route('actors.index')
                ->with('error', "Nem sikerült frissíteni: " . $e->getMessage());
        }
    }


    public function destroy($id)
    {
        try {
            $response = Http::api()
                ->withToken($this->token)
                ->delete("/actors/$id", ['id' => $id]);

            if ($response->failed()) {
                $message = $response->json('message') ?? 'Nem sikerült törölni a megyét.';
                return redirect()
                    ->route('actors.index')
                    ->with('error', "Hiba: $message");
            }

            $body = $response->json();
            $name = $body['name'] ?? 'Ismeretlen';

            return redirect()
                ->route('actors.index')
                ->with('success', "$name megye sikeresen törölve!");

        } catch (\Exception $e) {
            return redirect()
                ->route('actors.index')
                ->with('error', "Nem sikerült kommunikálni az API-val: " . $e->getMessage());
        }
    }
}
